import React, { useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

import axios from 'axios'
// import image from "../Photos/cyberpunk1.png"

const OtpPage = () => {
  const [otp, setOtp] = useState(['', '', '', '']);
  const otpInputs = [useRef(), useRef(), useRef(), useRef()];

  const navigate = useNavigate()
  const location = useLocation()

  let email = ''// location.state.email
  let password = ''// location.state.password
  let username= '' //location.state.username

  const handleOtpChange = (e, index) => {
    const value = e.target.value.replace(/\D/g, ''); // Allow only digits
    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);

    // Move to the next input
    if (value !== '' && index < otp.length - 1) {
      otpInputs[index + 1].current.focus();
    }
  };

  const handleVerifyClick = () => {
    const enteredOtp = otp.join('');

    // Check if there are the data or user
    // pressed submit button.
    if (!enteredOtp) {
        return 
    }

    // IP with tailscale.
    axios.put("http://100.91.43.32:4000/user/register", { username, password, email, insertedOTP: enteredOtp })
    .then(response => {
        navigate("/login", { replace: true })
    })
    .catch(error => {
        console.log(error)
    })
  };

  const handleResend = () => {
  };

  return (
    <div className='flex justify-center bg-gradient-to-b from-start to-end h-screen w-screen'>
      <div className="flex flex-col gap-8 bg-wtrans1 rounded-3xl justify-center items-center pt-12" style={{width:"400px" , height:"400px "}}>
        <h2 className='text-white font-nunito text-5xl font-bold select-none'> Enter 4-digit OTP </h2>
        <div className="flex flex-row items-center gap-3">
          {otp.map((digit, index) => (
            <input
              key={index}
              type="text"
              maxLength="1"
              value={digit}
              onChange={(e) => handleOtpChange(e, index)} className='w-10 h-14'
              ref={otpInputs[index]}
            />
          ))}
        </div>
        <div className="flex flex-row gap-8">
              <p>Didn't find the OTP?</p>
              <p style={{cursor:"pointer" , fontWeight:"bold"}} onClick={handleResend}>Resend OTP</p>
          </div>
        <button className="rounded-3xl w-24" onClick={handleVerifyClick} style={{backgroundColor : 'beige'}}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#ADD8E6'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'beige'}>Verify OTP</button>
      </div>
    </div>
  );
};

export default OtpPage;
