import React,{useState} from "react"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';

function FriendsCompAdd(props){
    return(
        <div className="flex flex-row select-none cursor-pointer hover:bg-purple-transparent rounded-2xl h-auto gap-4 align-center items-center" style={{width:"200px"}}>
            <div className="h-10 w-10 ml-2 rounded-full">
                <img src={props.icon} alt={props.name} className="h-full w-full rounded-full" />
            </div>
            <div className="flex flex-col">
                <p>{props.name}</p>
                <div className="flex flex-row gap-2">
                    <button className="text-white bg-purple-900 rounded-md pl-1 pr-1 cursor-pointer">Accept</button>
                    <p className="text-white ml-1 bg-gray-700 rounded-md pl-1 pr-1">Reject</p>
                </div>
            </div>
        </div>
    )
}
export default FriendsCompAdd;
