import React,{useState} from "react";
import Message from "./message";

function MessageBox (){

    const[chat,setChat]=useState([ 
        {   
            MessageID:"1",
            name:"Nti",
            RecipientID:"2",
            ConversationID:"1",
            Date:"10-02-2024",
            Time:"12:01:04",
            image:"",
            messageContent:"Hello friends"
        },
        {
            MessageID:"2",
            name:"Kostas",
            RecipientID:"1",
            ConversationID:"1",
            Date:"10-02-2024",
            Time:"12:01:10",
            image:"",
            messageContent:"Hello Friend2"
        },
        {   
            MessageID:"1",
            name:"Nti",
            RecipientID:"2",
            ConversationID:"1",
            Date:"10-02-2024",
            image:"",
            Time:"12:01:04",
            messageContent:"Hello Friend3"
        },
        {
            MessageID:"2",
            name:"Kostas",
            RecipientID:"1",
            ConversationID:"1",
            Date:"10-02-2024",
            image:"",
            Time:"12:01:10",
            messageContent:"Hello Friend4"
        },
    ])
    return (
        <div className="h-full w-full">
            {chat.map(message =>(
                <div className="flex flex-col overflow-y-auto">
                    <Message key={message.MessageID}
                            message={message}/>
                </div>
            ))}
        </div>
    )
}

export default MessageBox
