import React, { useState } from 'react';

// Assuming FontAwesomeIcon and faPause are used elsewhere or will be used
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPause, faShuffle, faPlay, faForwardStep, faBackwardStep, faRepeat } from '@fortawesome/free-solid-svg-icons';

import AlbumCover from "../Photos/Eminem_album_cover.jpg"

// Import custom style for the bar handle
import '../styles/handle.css';

function MusicBar(song) {
	const [value, setValue] = useState(0)
	const [randomSelected, setRandomSelected] = useState(false)

	// Ensure there's always a small segment for the purple to show at the beginning
	const percentage = value === 0 ? 0 : (value / song.duration) * 100

	// Change the random play selection.
	const changeRandom = () => {
		setRandomSelected(!setRandomSelected)
	}

	// Updated sliderStyle to correctly reflect the initial appearance and dynamic changes
	const sliderStyle = {
	  background: value === 0 ? '#393e46' : `linear-gradient(to right, #9818d6 ${percentage}%, #393e46 ${percentage}%)`, // This ensures the right side starts with #393e46
	  cursor: 'pointer',
	  appearance: 'none',
	  width: '100%',
	  height: '6px',
	  borderRadius: '9999px',
	};

	return (
		<div className='flex flex-col w-full justify-center items-center'>
			<div className='flex flex-row w-full justify-center items-center gap-5'>
				<FontAwesomeIcon className={`${(randomSelected) ? 'text-gray-200' : 'text-purple-200'} hover:text-white w-5 h-5 cursor-pointer select-none`} icon={faShuffle} onClick={changeRandom} />
				<FontAwesomeIcon className='text-gray-200 hover:text-white w-5 h-5 cursor-pointer select-none' icon={faBackwardStep} />
				<div className='relative'>
					<div className='absolute bg-purple-400 w-8 h-8 rounded-full p-2 flex justify-center items-center' style={{top: '-16px'}}>    
						<FontAwesomeIcon className='text-white hover:w-6 hover:h-6 w-5 h-5 cursor-pointer select-none' icon={faPause} />
					</div>
				</div>
				<div className='ml-3'> </div> {/* / Make space between two icons. */}
				<FontAwesomeIcon className='text-gray-200 hover:text-white w-5 h-5 cursor-pointer select-none' icon={faForwardStep} />
				<FontAwesomeIcon className='text-gray-200 hover:text-white w-5 h-5 cursor-pointer select-none' icon={faRepeat} />
			</div>
			<div className="flex flex-row w-full mt-5 px-4">
				<input
					type="range"
					min="0"
					max={song.duration}
					step="1"
					value={value}
					onChange={(e) => setValue(e.target.value)}
					style={sliderStyle}
					className='slider-thumb'
				/>
			</div>
		</div>
	);
}

export default MusicBar;
