import React,{useState,useEffect} from "react";
import Albums from "../mainSpace/album";
import Artists from "../mainSpace/artists";
import Favorites from "../mainSpace/favorites";
import image from "../..//Photos/image.png"
import AlbumSongs from "../albumComponent/albumSongs";

function MainSpace () {
    const [choice , setChoice] = useState("Albums");
    const [selectedAlbum, setSelectedAlbum] = useState(null);

    const handleChoice = (items) =>{
        setChoice(items);
    }

    const handleAlbumClick = (album) => {
        setSelectedAlbum(album); // Set selected album
        setChoice("AlbumSongs"); // Switch to AlbumSongs component
        prompt({selectedAlbum})
      };

    const renderComponent = () => {
        switch (choice) {
            case 'Albums':
                return <Albums 
                    image ={image} setSelectedAlbum={setSelectedAlbum} setChoice={setChoice} 
                />;
            case 'Favorites':
                return <Favorites/>;
            case 'Artists':
                return <Artists/>;
            case 'AlbumSongs':
                return <AlbumSongs
                        image = {image}
                        title = {selectedAlbum.title}
                        artist= {selectedAlbum.artist}
                        year = {selectedAlbum.year}
                        gerne = {selectedAlbum.gerne}
                />; 
            default:
                return null;
        }
    }
    return(
        <div className="flex flex-col h-full">
            <div className = "flex flex-row gap-12 text-white justify-center mt-5 mr-4 font-exo text-xl mb-6">
                <p className = "cursor-pointer" onClick={() => handleChoice("Artists")} style={{color:choice==="Artists"?"#ad56cd":"white",borderBottom: choice === "Artists" ? "2px solid #ad56cd" : ""}}>Artists</p>
                <p className = "cursor-pointer " onClick={() => handleChoice("Albums")} style={{color:choice==="Albums"?"#ad56cd":"white",borderBottom: choice === "Albums" ? "2px solid #ad56cd" : ""}}>Albums</p>
                <p className = "cursor-pointer" onClick={() => handleChoice("Favorites")} style={{color:choice==="Favorites"?"#ad56cd":"white",borderBottom: choice === "Favorites" ? "2px solid #ad56cd" : ""}}>Favorites</p>

            </div>
            <hr className="text-white mr-16 ml-16"></hr>
            <div className="h-5/6  rounded-3xl mr-4 ml-4 mt-4">
                {renderComponent()}
            </div>
        </div>
    )
}

export default MainSpace
