import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';

import { RenderContext } from '../../Pages/musicHome';
import { ToastContainer, toast } from 'react-toastify';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import MessageBar from '../chat/messagebar';
import Message from '../chat/message';

function Chat(props) {
    const { socket } = useContext(RenderContext);
    const [messages, setMessages] = useState([]);
    const [id, setID] = useState();
    const [chatChange, setChatChange] = useState(false)

    useEffect(() => {
        const accessToken = localStorage.getItem('accessToken');
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

        axios.post('http://100.91.43.32:4000/chat/get-conversation-id', { friend: props.friendsName })
            .then((response) => {
                setID(response.data.conversationID);

                socket.emit('conversation', response.data.conversationID);

                const messageHandler = (message) => {
                    // Update a separate state variable to force re-render
                    setMessages((prevMessages) => [...prevMessages, message]);
                };

                // Set up socket event listener
                socket.on('message', messageHandler);

                // Return cleanup function to remove event listener
                return () => {
                    socket.off('message', messageHandler);
                };
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }, []); // empty dependency array means this effect runs only once after the component mounts

    // Go to main page.
    const musicHome = () => {
        if (props) {
            props.setChoice('mainSpace');
        }
    };

    return (
        <div className="flex flex-col text-white h-full w-full rounded-3xl">
            <div className="flex flex-row gap-4 items-center text-xl ml-4 pt-4">
                <FontAwesomeIcon icon={faArrowLeft} className="cursor-pointer" onClick={musicHome} />
                <p> {props.friendsName} </p>
            </div>
            <div className="mt-4 h-4/5 w-4/6 mx-auto overflow-y-auto">
                {messages.map((message, index) => (
                    // Check if message exists and is not empty
                    // You might need to adjust this condition based on your message structure
                    message && Object.keys(message).length !== 0 && <Message key={index} message={message} />
                ))}
            </div>
            <div className="py-5 rounded-b-3xl w-full">
                <MessageBar conversationID={id} />
            </div>
            <ToastContainer />
        </div>
    );
}

export default Chat;
