import React,{ useState, useEffect } from "react";

import axios from "axios"

import FriendsComp from "./friendComponent/friendComp";
import image1 from "../..//Photos/image.png"

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserPlus } from '@fortawesome/free-solid-svg-icons';

function FriendsBasic (props){
    const [friends, setFriends] = useState ('')

    useEffect(() => {
        const token = localStorage.getItem('accessToken');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`

        // IP with tailscale.
        axios.get("http://100.91.43.32:4000/user/get-friends")
        .then(response => {
            console.log(response)
            setFriends(response.data.friends)
        })
        .catch()
    }, [])
    
    // Change to friend request.
    const addFriends=()=>{
        props.setFriendsPage("friendsAdd");
    }

    // Open the chat.
    const openChat=(name)=>{
        props.setRender("chat")
        props.setFriend(name)
    }


    return(
        <div className="flex select-none flex-col h-full">
            <div className="flex flex-row text-xl ml-5 mr-5 mt-5">
                <p className="font-exo text-white">Friends</p>
                <FontAwesomeIcon icon={faUserPlus} className="text-white ml-auto mt-1 cursor-pointer text-lg" onClick={() => addFriends()} />
            </div>
            <hr></hr>
            <div className="text-white flex flex-row flex-wrap justify-center gap-4 overflow-y-auto h-5/6 mt-5">
                {/* {friends.map((friend,index) => (
                <div key={index} onClick={() => openChat(friend.name)}>
                    <FriendsComp 
                        icon = {image1}
                        name = {friend.name} 
                    />
                </div>
                ))} */}
            </div>
        </div>
    )
}

export default FriendsBasic;
