import React,{useState} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';

import FriendsCompAdd  from "./friendComponent/friendsCompAdd";
import image1 from "../..//Photos/image.png"

function FriendsAdd (props){
    
    const [friendRequests, setFriendRequests] = useState ()

    const friendsB = () =>{
        props.setFriendsPage("friendsBasic");
    }

    return(
        <div className="flex flex-col h-full select-none">
            <div className="flex flex-row text-xl ml-5 mr-5 mt-5">
                <div className="flex flex-row gap-2">
                    <div>
                        <FontAwesomeIcon icon={faArrowLeft} className="cursor-pointer" onClick = {()=>friendsB()}/>
                    </div>   
                    <p className="font-exo text-white">Friend requests</p>
                </div>
            </div>
            <hr></hr>
            <div className="text-white flex flex-row flex-wrap justify-center gap-4 overflow-y-auto h-5/6 mt-5">
                {friendRequests.map((friend,index) => (
                <div key={index}>
                    <FriendsCompAdd 
                        icon = {friend.icon}
                        name = {friend.name} 
                    />
                </div>
                ))}
            </div>
            
        </div>
    )
}

export default FriendsAdd;
