import React, { useState, useContext } from "react"

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-solid-svg-icons' 

function UserDropdownMenu () {
    const [isOpen, setIsOpen] = useState(false)
    const [userPhoto, setUserPhoto] = useState()
    const {choosePage} = useContext(RenderContext)
  
    // Function to toggle the dropdown menu
    const toggleDropdown = () => {
      setIsOpen(!isOpen);
    }
  
    const openAccount = () => {
        choosePage('user')
    }

    const logOut = () => {
    }

    return (
        <div className="relative pt-6 pr-10">
            { (userPhoto) ?
                <img 
                    onClick={toggleDropdown} 
                    className="text-grey-400 rounded-full transform -translate-x-1/2 hover:text-grey-500 hover:cursor-pointer hover:border-grey-500 z-10 w-10 h-10"
                    src={userPhoto}
                />
                : 
                <FontAwesomeIcon 
                    icon={faUser} 
                    onClick={toggleDropdown}   
                    className="text-white border border-rounded p-1 rounded-full transform -translate-x-1/2 hover:text-grey-500 hover:cursor-pointer hover:border-grey-500 z-10 w-5 h-5" 
                />
            }

            {/* Dropdown content */}
            {isOpen && (
                <div className="absolute top-full right-4 mt-2 w-48 bg-gray-500 shadow-md rounded overflow-hidden">
                    <ul className="list-none p-0 m-0">
                    <li onClick={openAccount} className="text-start text-white hover:bg-purple-200 cursor-pointer py-2 px-4 select-none"> Account </li>
                    <li className="text-start text-white hover:bg-purple-200 cursor-pointer py-2 px-4 select-none"> Playlists </li>
                    <hr/>
                    <li className="text-start text-white hover:bg-purple-200 cursor-pointer pb-2 pt-2 px-4 select-none" onClick={logOut} > Log out </li>
                    </ul>
                </div>
            )}

        </div>
    );
}

export default UserDropdownMenu;
