import React,{useState,useEffect} from "react";
import ArtistComp from "../artistsComponent/artistsComponent";

function Artists(props) {
    const [allArtists, setAllArtists] = useState([
        {
            name: "Pink Floyd",
            artist: "artist",
            music: ["Progressive Rock", "Psychedelic Rock"],
            famousSongs: ["Comfortably Numb", "Wish You Were Here", "Another Brick in the Wall"]
        },
        {
            name: "The Beatles",
            artist: "artist",
            music: ["Rock", "Pop"],
            famousSongs: ["Hey Jude", "Let It Be", "Yesterday"]
        },
        {
            name: "Miles Davis",
            artist: "artist",
            music: ["Jazz", "Fusion"],
            famousSongs: ["So What", "All Blues", "Freddie Freeloader"]
        },
        {
            name: "Beethoven",
            artist: "",
            music: ["Classical"],
            famousSongs: ["Symphony No. 9", "Für Elise", "Moonlight Sonata"]
        },
        {
            name: "Bob Dylan",
            artist: "artist",
            music: ["Folk", "Rock"],
            famousSongs: ["Like a Rolling Stone", "Blowin' in the Wind", "The Times They Are a-Changin'"]
        },
        {
            name: "Led Zeppelin",
            artist: "artist",
            music: ["Hard Rock", "Blues Rock"],
            famousSongs: ["Stairway to Heaven", "Whole Lotta Love", "Kashmir"]
        },
        {
            name: "Nina Simone",
            artist: "artist",
            music: ["Jazz", "Blues"],
            famousSongs: ["Feeling Good", "I Put a Spell on You", "My Baby Just Cares for Me"]
        },
        {
            name: "David Bowie",
            artist: "artist",
            music: ["Rock", "Art Rock"],
            famousSongs: ["Space Oddity", "Heroes", "Life on Mars?"]
        },
        {
            name: "Elvis Presley",
            artist: "artist",
            music: ["Rock and Roll", "Pop"],
            famousSongs: ["Hound Dog", "Jailhouse Rock", "Love Me Tender"]
        },
        {
            name: "Prince",
            artist: "artist",
            music: ["Funk", "Pop"],
            famousSongs: ["Purple Rain", "Kiss", "When Doves Cry"]
        }
    ]);
    const handleArtistClick = (artist) => {
       props.setSelectedArtist(artist); // Set selected album
        props.setChoice("ArtistsSongs");
    };
    return (
        <div className="flex flex-col overflow-x-auto h-full">
            <div className="text-white mb-4 text-center">
                <p className="font-bold">Artists by Music Genre</p>
                <p className="text-gray">Explore artists by the kind of music they support</p>
            </div>
            <div className="text-white flex flex-row justify-center flex-wrap gap-8 overflow-y-auto h-full">
                {allArtists.map((artist, index) => (
                    <div key={index} className="" onClick={() => handleArtistClick(artist)}>
                        <ArtistComp 
                            image={props.image}
                            name={artist.name}
                            artist={artist.artist}
                            music={artist.music}
                            famousSongs={artist.famousSongs}
                        />
                    </div>
                ))}
            </div>
        </div>
    
);

}

export default Artists;
