import React,{useState} from 'react'
import { RenderContext } from '../../Pages/musicHome';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';

function MessageBar () {
    const[value,setValue] = useState ("");

    const handleSendMesage = (e) => {
        e.preventDefault();



        setValue("");
    }

    return(   
        <div className="">
            <form onSubmit={handleSendMesage} className='containerWrap flex items-center justify-center flex-row gap-8'>
                <input value={value} onChange={e=>setValue(e.target.value)} className='input pl-4 w-4/6 text-black focus:outline-none bg-gray-100 rounded-xl h-8' type="text">

                </input>
                <button>
                    <FontAwesomeIcon type ="submit" icon={faPaperPlane} className="text-2xl" />
                </button>
            </form>
        </div>            

    )
}
export default MessageBar
