import React,{useState} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import RecentSongs from '../songComponent/songComponent';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import Albums from '../albumComponent/albumComponent'

function ArtistsSongs (props){
	const [song, setSong] = useState([{
			title:"The Business",
			time:"3:42"
		},
		{
			title:"I'm Good(Blue)",
			time:"3:12"
		},
		{
			title:"Whistle",
			time:"3:25"
		},
		{
			title:"Hall of Fame",
			time:"2:42"
		},
		{
			title:"Bones",
			time:"3:33"
		},
		{
			title:"The  Nights",
			time:"3:26"
		}
	])

	const [artistAlbum,setArtitsAlbum] = useState([{
		title: "Echoes",
		artist: "Pink Floyd",
		genre: "Progressive Rock",
		year: 1971,
		time:"40:32",
		tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
	},
	{
		title: "Thriller",
		artist: "Michael Jackson",
		genre: "Pop",
		year: 1982,
		time:"40:32",
		tracks: ["Wanna Be Startin' Somethin'", "Baby Be Mine", "The Girl Is Mine", "Thriller", "Beat It", "Billie Jean", "Human Nature", "P.Y.T. (Pretty Young Thing)", "The Lady in My Life"]
	},
	{
		title: "Back in Black",
		artist: "AC/DC",
		genre: "Hard Rock",
		year: 1980,
		time:"40:32",
		tracks: ["Hells Bells", "Shoot to Thrill", "What Do You Do for Money Honey", "Given the Dog a Bone", "Let Me Put My Love into You", "Back in Black", "You Shook Me All Night Long", "Have a Drink on Me", "Shake a Leg", "Rock and Roll Ain't Noise Pollution"]
	},
	{
		title: "The Dark Side of the Moon",
		artist: "Pink Floyd",
		genre: "Progressive Rock",
		year: 1973,
		time:"40:32",
		tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
	}
	])

	const handleAlbumClick = (album) => {
		props.setSelectedAlbum(album); // Set selected album
		props.setChoice("AlbumSongs");
	};

	//handle back artist button
	const goToArtists =()=>{
		if(props) {
			props.setChoice("Artists");
		}
	}

	return (
		<div className ="flex flex-col h-full overflow-y-auto select-none">
			<div className="text-white text-3xl ml-10 cursor-pointer">
				<FontAwesomeIcon icon={faArrowLeft} onClick={goToArtists}/>
			</div> 
			<div className="flex flex-row w-5/6 mx-auto">
				<img src={props.image} className="h-52 w-52 rounded-full "/>
				<div className="flex flex-col mt-20">
					<div>
						<h5 className="ml-6 text-white card-title">Artist</h5>
					</div>
					<div className="text-white font-exo text-6xl ml-8">
						{props.name}
					</div>
					<div className="ml-6 flex flex-row gap-4 text-white card-title">
						<div className="flex flex-row gap-2 ">    
							<h5>{song.length} </h5>
							<h5>Songs</h5>
							<p>‚óè</p>
						</div>
						<div className="flex flex-row gap-2 ">
							<h5>{artistAlbum.length}</h5>
							<h5>Albums</h5>
						</div>
					</div>
				</div>
			</div>
			<div className="flex flex-col select-none ">
				<div className="font-exo text-2xl text-white ml-16 mt-10">
					Artist's most heard
				</div>
				<div className="flex flex-row flex-wrap text-white gap-4 overflow-x-auto justify-center mt-8">
					{song.map((song, index) => (
						<div key={index} className="text-white">
							<RecentSongs 
								image = {props.image}
								title = {song.title}
								artist = {song.time}
							/>
						</div>
					))}
				</div>
				<div className="font-exo text-2xl text-white ml-16 mt-10">
					Artist's Albums
				</div>
				<div className="flex flex-row flex-wrap text-white gap-16 overflow-x-auto justify-center mt-8">
					{artistAlbum.map((album,index) => (
						<div key={index} className="" onClick={() => handleAlbumClick(album)}>
							<Albums 
								image = {props.image}
								artist = {album.artist}
								title = {album.title}
								genre ={album.genre}
								year = {album.year}
							/>
						</div>
					))}
				</div>
			</div>
		</div>
	)
}

export default ArtistsSongs;
