import React from 'react';

const Slider = ({ value, onChange }) => {
  const percent = value * 100;

  return (
    <div className="flex items-center justify-center h-full">
      <div className="w-full max-w-lg px-4">
        <input
          className="slider-thumb w-full h-1 rounded-full cursor-pointer appearance-none bg-gray-300"
          type="range"
          onChange={onChange}
          value={value}
          style={{
            background: `linear-gradient(to right, #E1E1E6 0%, #E1E1E6 ${percent}%, #A5AAB2 ${percent}%, #A5AAB2 100%)`,
          }}
        />
      </div>
    </div>
  );
};

export default Slider;
