import React, { useState } from 'react';

import '../styles/handle.css'; // Ensure your custom CSS is correctly imported

const SliderWithProgress = ({ initialValue = 0 }) => {
  const [value, setValue] = useState(initialValue);

  // Calculate the percentage of the slider value for the gradient
  // This needs to be defined before using it in `sliderStyle`
  const percentage = (value / 1.0) * 100; // Assuming the value range is 0 to 1

  // Inline styles for dynamically updating the slider's background and making it rounded
  const sliderStyle = {
    background: `linear-gradient(to right, purple ${percentage}%, purple ${percentage}%)`,
    cursor: 'pointer',
    appearance: 'none',
    width: '100%',
    height: '8px', // Adjust the track height as needed
    borderRadius: '9999px', // Fully rounded edges
  };

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <input
        type="range"
        min="0"
        max="1"
        step="0.01"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        className="slider-thumb"
        style={sliderStyle}
      />
    </div>
  );
};

export default SliderWithProgress;
