import React,{useState} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import image1 from "../../Photos/image.png"
import Song from "./song";

function AlbumSongs (props){
    const[tracks,setTracks] = useState([
        {
            name : "Speak to Me",
            time:"3:22",
            image:{image1},
            artist:"John"
        },
        { 
            name :"Breathe",
            time:"3:22",  
            image:{image1},
            artist:"John"
        },
        {
            name :"On the Run",
            time:"3:22",
            image:{image1},
            artist:"John"
        },
        {
            name :"Time",
            time:"3:22",
            image:{image1},
            artist:"John"
        }, 
        {
            name :"The Great Gig in the Sky",
            time:"3:22",
            image:{image1},
            artist:"John"
        }, 
        {
            name :"Money",
            time:"3:22",
            image:{image1},
            artist:"John"
        }, 
        {
            name :"Us and Them",
            time:"3:22",
            image:{image1},
            artist:"John"
        },
        {
            name : "Any Colour You Like",
            time:"3:22",
            image:{image1},
            artist:"John"
        }, 
        {
            name :"Brain Damage",
            time:"3:22",
            image:{image1},
            artist:"John"
        },
        {
            name : "Eclipse",
            time:"3:22",
            image:{image1},
            artist:"John"
        }
    ])   
    const getTotalDuration = () => {
        let totalSeconds = 0;
        tracks.forEach(track => {
            const [minutes, seconds] = track.time.split(":").map(Number);
            totalSeconds += minutes * 60 + seconds;
        });
        const totalMinutes = Math.floor(totalSeconds / 60);
        const remainingSeconds = totalSeconds % 60;
        return `${totalMinutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
    };
    
    //handle button back click
    const goToAlbums =()=>{
		if(props)
		props.setChoice("Albums");
	}

    return (
        <div className ="flex flex-col h-full overflow-y-auto">
            <div className="text-white text-3xl ml-10 cursor-pointer">
                <FontAwesomeIcon icon={faArrowLeft} onClick={goToAlbums} />
            </div> 
            <div className="flex flex-row w-5/6 mx-auto">
                <img src={props.image} className="h-50 w-2/5 rounded-2xl"/>
                <div className="flex flex-col mt-20">
                    <div className="mb-2">
                        <h5 className="ml-6 text-white card-title">Album</h5>
                    </div>
                    <div className="text-white font-exo font-bold text-6xl ml-6 mb-1">
                        {props.title}
                    </div>
                    <div className="ml-6 flex flex-row text-gray-200 gap-2  font-exo text-l">
                        <div>
                            <p>Songs {tracks.length}</p>
                        </div>
                        <div>
                            ● 
                        </div>
                        <div>
                            Total Duration {getTotalDuration()}
                        </div>
                    </div>
                </div>
            </div>
            <div className="mt-8 w-5/6 mx-auto flex flex-col gap-8 cursor-pointer">
                {tracks.map((track ) => (
                    <div key={track}>
                        <Song 
                            name={track.name}
                            time ={track.time}
                            image={props.image}
                            artist={track.artist}
                            />
                    </div>
                ))}
            </div>
        </div>
    )
}

export default AlbumSongs;
