import React, { useEffect, useState } from "react"
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom"
import axios from "axios"

// Import Pages.
import HomePage from "./Pages/homePage"
import Login from "./Pages/logIn"
import SignUp from "./Pages/signup"
import CV from "./Pages/cv"
import MusicHome from "./Pages/musicHome"
import Otp from "./Pages/otpPass"

// Basic routing process.
function App () {
    const [initialPage, setInitialPage] = useState('/')

    useEffect(() => {
        const accessToken = localStorage.getItem("accessToken")
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`

        axios.get("http://100.91.43.32:4000/token/check-token")
        .then(
            setInitialPage("/musicHome")
        )
        .catch(
            axios.get("http://100.91.43.32:4000/token/refresh-token")
            .then(response => {
                localStorage.setItem("accessToken", response.data.accessToken)

                setInitialPage("/musicHome")
            })
        )
    }, [])

    return (
        <BrowserRouter>
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="login" element={<Login />} />
                <Route path="register" element={<SignUp />} />
                <Route path="cv" element={<CV />} />
                <Route path="musicHome" element={<MusicHome />} />
                <Route path="otp" element={<Otp />} />

                <Route path="*" element={<Navigate to={initialPage} />} />
            </Routes>
        </BrowserRouter>
    )
}

export default App
