import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./Pages/homePage";
import Login from "./Pages/logIn";
import SignUp from "./Pages/signup";
import CV from "./Pages/cv";
import MusicHome from "./Pages/musicHome";
import Otp from "./Pages/otpPass";

function App() {
  const [initialPage, setInitialPage] = useState("/");

  useEffect(() => {
    // Simulating token check logic with setTimeout
    const timer = setTimeout(() => {
      setInitialPage("/musicHome"); // Set initial page to musicHome after timeout
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {initialPage !== "/" && <Navigate to={initialPage} replace />}
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<SignUp />} />
          <Route path="/cv" element={<CV />} />
          <Route path="/musicHome" element={<MusicHome />} />
          <Route path="/otp" element={<Otp />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App
