import React, { useState, useRef } from "react"

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faVolumeMute, faVolumeDown } from '@fortawesome/free-solid-svg-icons'

import "../styles/handle.css"

function AudioBar () {
    const [volume, setVolume] = useState(1)
    const audio = useRef(null)

    const handleVolumeChange = (e) => {
        const newVolume = parseFloat(e.target.value)
        setVolume(newVolume)
        if (audio.current) {
            audio.current.volume = newVolume
        }
    }

    // Updated sliderStyle to correctly reflect the initial appearance and dynamic changes
	const sliderStyle = {
        background: volume === 0 ? '#393e46' : `linear-gradient(to right, #9818d6 ${volume*100}%, #393e46 ${volume*100}%)`, // This ensures the right side starts with #393e46
        cursor: 'pointer',
        appearance: 'none',
        width: '100%',
        height: '6px',
        borderRadius: '9999px',
    };

    return (
        <div className="w-full flex flex-row items-end h-full">
            <div className="mt-5 relative mr-4">    
                <FontAwesomeIcon icon={faVolumeUp} />
            </div>
            <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={volume}
                onChange={handleVolumeChange}
                style={sliderStyle}
                className="slider-thumb"
            />
        </div>
    )
}

export default AudioBar
