import React from "react";

import MusicBar from "./musicBar";

import AlbumCover from "../Photos/Eminem_album_cover.jpg";
import AudioBar from "./audioBar";

function MusicPlayer (song) {
  return (
    <div className="fixed bottom-0 w-full h-24 bg-black-200 flex items-center justify-between">
      <div className="flex flex-row items-center ml-5">
        <img src={AlbumCover} className="w-16 select-none" alt="Album Cover" />
        <div className="flex flex-col justify-center items-start pl-3">
            <h3 className="text-white text-md font-exo cursor-pointer hover:underline select-none"> {song.name} </h3>
            <p className="text-gray-200 text-sm font-exo cursor-pointer hover:underline select-none"> {song.artist} </p>
        </div>
      </div>
      <div className="flex justify-center items-center w-1/3">
        <MusicBar duration={436 * 60} />
      </div>
      <div className="mr-20"> <AudioBar /> </div>
    </div>
  );
}

export default MusicPlayer;
