import React, { useState, useEffect, useRef, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import { jwtDecode } from 'jwt-decode';

import { RenderContext } from "../Pages/musicHome";

import MusicBar from "./musicBar";

import AudioBar from "./audioBar";

function MusicPlayer (songInfo) {
    const [song, setSong] = useState()
    const [songImage, setSongImage] = useState()
    const [songTitle, setSongTitle] = useState()
    const [songArtist, setSongArtist] = useState()
    const audioRef = useRef(null)

    const navigate = useNavigate()

    const { socket } = useContext(RenderContext)

    // Get username from the token.
    const getUsernameFromToken = () => {
        const accessToken = localStorage.getItem('accessToken');
        if (!accessToken) {
            return null;
        }
    
        try {
            const decodedToken = jwtDecode(accessToken)
            return decodedToken.username; // Assuming the username is stored in the token under 'username'
        } catch (error) {
            const refreshToken = localStorage.getItem('refreshToken');
            axios.defaults.headers.common['Authorization'] = `Bearer ${refreshToken}`

            axios.get("http://100.91.43.32:4000/token/refresh-token")
            .then(response => {
                localStorage.setItem("accessToken", response.data.accessToken)
                const decodedToken = jwtDecode(response.data.accessToken)
            
                return decodedToken.username; // Assuming the username is stored in the token under 'username'
            })
            .catch(error => {
                navigate("/", { replace: true })
            })
            
            return null;
        }
    }

    useEffect(() => {

    }, [songInfo])

    // Get the requested song from the database.
    useEffect(() => {
        if (songInfo.songPlaying) {
            const username = getUsernameFromToken()

            // Update a separate state variable to force re-render.
            const audioHandle = (data) => {
                console.log(data);
                const blob = new Blob([data], { type: 'audio/mp3' });
                const url = URL.createObjectURL(blob);
                setSong(url);
            };

            socket.emit('listenSong', username, songInfo.songPlaying)

            socket.on('getSong', audioHandle)
        }
    }, [songInfo.songPlaying]);    

  return (
    <div className="fixed bottom-0 w-full h-24 bg-black-200 flex items-center justify-between">
      <div className="flex flex-row items-center ml-5">
        { songInfo.image ? <img src={songInfo.image} className="w-16 select-none" alt="Album Cover" /> : <div className="ml-36"> </div> }
        <div className="flex flex-col justify-center items-start pl-3">
            <h3 className="text-white text-md font-exo cursor-pointer hover:underline select-none"> {songInfo.title} </h3>
            <p className="text-gray-200 text-sm font-exo cursor-pointer hover:underline select-none"> {songInfo.artist} </p>
        </div>
      </div>
      <div className="flex justify-center items-center w-1/3">
        <MusicBar audioRef={audioRef} song={song} setSongPlaying={songInfo.setSongPlaying} songInfo={songInfo} />
      </div>
      <div className="mr-20"> <AudioBar audio={audioRef} /> </div>
    </div>
  );
}

export default MusicPlayer;
