import React, {useState, useEffect, useRef} from "react";

import axios from "axios";

import MusicBar from "./musicBar";

import AlbumCover from "../Photos/Eminem_album_cover.jpg";
import AudioBar from "./audioBar";

function MusicPlayer (songInfo) {
    const [song, setSong] = useState('')
    const [songData, setSongData] = useState('')
    const [isPlaying, setIsPlaying] = useState(false)
    const audioRef = useRef(null)

    // Get the requested song from the database.
    useEffect(() => {
        if (!songInfo.songPlaying) {
            console.log(song)

            return
        }

        const token = localStorage.getItem('token');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`

        // IP with tailscale.
        axios.get("http://100.91.43.32:4000/song/get-song?songTitle=" + songInfo.songPlaying, { responseType: 'blob' })
            .then(response => {
                const url = URL.createObjectURL(response.data);
                setSong(url)
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, [songInfo.songPlaying]);    

    //  // Get the requested song from the database.
    //  useEffect(() => {
    //     if (!songInfo.songPlaying) {
    //         console.log(songData)

    //         return
    //     }

    //     const token = localStorage.getItem('token');
    //     axios.defaults.headers.common['Authorization'] = `Bearer ${token}`

    //     // IP with tailscale.
    //     axios.get("http://100.91.43.32:4000/song/get-song-data?songTitle=" + songInfo.songPlaying)
    //         .then(response => {
    //             setSongData(response.data)
    //             console.log(songData, response.data)
    //         })
    //         .catch(error => {
    //             console.error('Error fetching data:', error);
    //         });
    // }, [songInfo.songPlaying])

  return (
    <div className="fixed bottom-0 w-full h-24 bg-black-200 flex items-center justify-between">
      <div className="flex flex-row items-center ml-5">
        <img src={songInfo.image} className="w-16 select-none" alt="Album Cover" />
        <div className="flex flex-col justify-center items-start pl-3">
            <h3 className="text-white text-md font-exo cursor-pointer hover:underline select-none"> {songInfo.title} </h3>
            <p className="text-gray-200 text-sm font-exo cursor-pointer hover:underline select-none"> {songInfo.artist} </p>
        </div>
      </div>
      <div className="flex justify-center items-center w-1/3">
        <MusicBar audioRef={audioRef} song={song} setIsPlaying={setIsPlaying} />
      </div>
      <div className="mr-20"> <AudioBar audio={audioRef} /> </div>
    </div>
  );
}

export default MusicPlayer;
