import React,{useState,useEffect} from "react";

import RecentSongs from '../searchRecentComponent/searchRecent.js'

import image from '../../Photos/image.png'

function Favorites(props) {

    const [AllRecentSongs, setAllRecentSongs] = useState([
        { title: "Shape of You", artist: "Ed Sheeran" },
        { title: "Blinding Lights", artist: "The Weeknd" },
        { title: "Dance Monkey", artist: "Tones and I" },
        { title: "Uptown Funk", artist: "Mark Ronson ft. Bruno Mars" },
        { title: "Closer", artist: "The Chainsmokers ft. Halsey" },
        { title: "Happier", artist: "Marshmello ft. Bastille" },
        // Add more songs as needed
    ]);

    const handleShowAllArtistsClick = () => {
        props.setChoice("All");
    };

    return(
        <div className=" flex-wrap overflow-y-auto h-full">
            <div className="text-white mb-4 text-center">
                <p className="font-bold">Find Your Favorite Songs</p>
                <p className="text-gray">Like you favorite music & find it here</p>
            </div>
            <div className="text-white mb-4 mt-8 ml-20 text-xl">
                <p className="font-bold">Your Favorite Songs :</p>
            </div>
            <div className="flex flex-row flex-wrap justify-center text-white gap-4 overflow-x-auto ml-4 mt-8">
                {AllRecentSongs.map((song, index) => (
                    <div key={index} className="text-white">
                        <RecentSongs 
                            image = {image}
                            title = {song.title}
                            artist = {song.artist}
                        />
                    </div>
                ))}
            </div>
            <div className="flex flex-row justify-between text-white mb-4 mt-12 ml-20 text-xl">
                <p className="font-bold">Discover picks for you :</p>
                <p className="mr-20 text-lg text-slate-300">
                    <a
                        href="#"
                        className="hover:text-white hover:underline"
                        onClick={handleShowAllArtistsClick} // Attach the click handler to the "Show all" link
                    >
                    Show more
                    </a>
                </p>
            </div>
            <div className="flex flex-row flex-wrap justify-center text-white gap-4 overflow-x-auto ml-4 mt-8">
                {AllRecentSongs.map((song, index) => (
                    <div key={index} className="text-white">
                        <RecentSongs 
                            image = {image}
                            title = {song.title}
                            artist = {song.artist}
                        />
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Favorites;
