import React, { useState } from "react";
import RecentSongs from '../songComponent/songComponent';
import Artists from '../artistsComponent/artistsComponent';
import Albums from '../albumComponent/albumComponent'
import eminem from '../../Photos/eminem.png'

function All(props) {
    const [AllRecentSongs, setAllRecentSongs] = useState([
    { title: "Shape of You", artist: "Ed Sheeran" },
    { title: "Blinding Lights", artist: "The Weeknd" },
    { title: "Dance Monkey", artist: "Tones and I" },
    { title: "Uptown Funk", artist: "Mark Ronson ft. Bruno Mars" },
    { title: "Closer", artist: "The Chainsmokers ft. Halsey" },
    { title: "Happier", artist: "Marshmello ft. Bastille" },
    // Add more songs as needed
    ]);

    const [allArtists, setAllArtists] = useState([
    {
        name: "Pink Floyd",
        artist: "artist",
        music: ["Progressive Rock", "Psychedelic Rock"],
        famousSongs: ["Comfortably Numb", "Wish You Were Here", "Another Brick in the Wall"]
    },
    {
        name: "The Beatles",
        artist: "artist",
        music: ["Rock", "Pop"],
        famousSongs: ["Hey Jude", "Let It Be", "Yesterday"]
    },
    {
        name: "Miles Davis",
        artist: "artist",
        music: ["Jazz", "Fusion"],
        famousSongs: ["So What", "All Blues", "Freddie Freeloader"]
    },
    {
        name: "Beethoven",
        artist: "",
        music: ["Classical"],
        famousSongs: ["Symphony No. 9", "Für Elise", "Moonlight Sonata"]
    },
    // {
    //     name: "Bob Dylan",
    //     artist: "artist",
    //     music: ["Folk", "Rock"],
    //     famousSongs: ["Like a Rolling Stone", "Blowin' in the Wind", "The Times They Are a-Changin'"]
    // },
    // {
    //     name: "Led Zeppelin",
    //     artist: "artist",
    //     music: ["Hard Rock", "Blues Rock"],
    //     famousSongs: ["Stairway to Heaven", "Whole Lotta Love", "Kashmir"]
    // }
    ]);

    const [allAlbums,setAllAlbums] = useState([{
        title: "Echoes",
        artist: "Pink Floyd",
        genre: "Progressive Rock",
        year: 1971,
        tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
        },
        {
        title: "Thriller",
        artist: "Michael Jackson",
        genre: "Pop",
        year: 1982,
        tracks: ["Wanna Be Startin' Somethin'", "Baby Be Mine", "The Girl Is Mine", "Thriller", "Beat It", "Billie Jean", "Human Nature", "P.Y.T. (Pretty Young Thing)", "The Lady in My Life"]
        },
        {
        title: "Back in Black",
        artist: "AC/DC",
        genre: "Hard Rock",
        year: 1980,
        tracks: ["Hells Bells", "Shoot to Thrill", "What Do You Do for Money Honey", "Given the Dog a Bone", "Let Me Put My Love into You", "Back in Black", "You Shook Me All Night Long", "Have a Drink on Me", "Shake a Leg", "Rock and Roll Ain't Noise Pollution"]
        },
        {
        title: "The Dark Side of the Moon",
        artist: "Pink Floyd",
        genre: "Progressive Rock",
        year: 1973,
        tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
        },
        // {
        // title: "Echoes",
        // artist: "Pink Floyd",
        // genre: "Progressive Rock",
        // year: 1971,
        // tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
        // },
        // Add more songs as needed
        ]);

    const addAll = () => {
        // // Logic to add a recently heard song
        // const newSong = { title: "New Song", artist: "New Artist" }; // Replace with actual song info
        // setAll([...all, newSong]); // Add the new song to the all array
    };

    const handleShowAllArtistsClick = () => {
         props.setChoice("Artists");
    };

    const handleShowAllAlbumsClick = () => {
        props.setChoice("Albums");
    };

    return (
        <div className=" flex-wrap overflow-y-auto h-full">
            <div className="text-white mb-4 mt-8 ml-14 text-xl">
                <p className="font-bold">Recently Played</p>
            </div>
            <div className="flex flex-row flex-wrap text-white gap-4 overflow-x-auto ml-4 mt-8">
                {AllRecentSongs.map((song, index) => (
                    <div key={index} className="text-white">
                        <RecentSongs 
                            image = {props.image}
                            title = {song.title}
                            artist = {song.artist}
                        />
                    </div>
                ))}
            </div>
            <div className="flex flex-row justify-between mb-4 mt-16 ml-14">
                <p className="font-bold text-white text-xl">Famous Artists</p>
                    <p className="mr-20 text-lg text-slate-300">
                        <a
                            href="#"
                            className="hover:text-white hover:underline"
                            onClick={handleShowAllArtistsClick} // Attach the click handler to the "Show all" link
                        >
                    Show all
                </a>
                </p>
            </div>
            <div className="flex flex-row flex-wrap text-white gap-4 overflow-x-auto ml-10 mt-8" >
                {allArtists.map((artist, index) => (
                    <div key={index} className="text-white">
                        <Artists 
                            image = {eminem}
                            name = {artist.name}
                            music = {artist.music}

                        />
                    </div>
                ))}
            </div>
            <div className="flex flex-row justify-between mb-4 mt-16 ml-14">
                <p className="font-bold text-white text-xl">Top Tier Albums</p>
                <p className="mr-20 text-lg text-slate-300">
                    <a href="#" className="hover:text-white hover:underline"
                       onClick={handleShowAllAlbumsClick}>
                        Show all
                    </a>
                </p>
            </div>
            <div className="flex flex-row flex-wrap text-white gap-4 overflow-x-auto ml-10 mt-8" >
                {allAlbums.map((album,index) => (
                    <div key={index} className="">
                        <Albums 
                            image = {props.image}
                            artist = {album.artist}
                            title = {album.title}
                            genre ={album.genre}
                            year = {album.year}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default All;
