import React,{useState} from 'react';

function Message (props){
    const[user,setUser] = useState("Kostas")
    return(
        <div className="text-white p-4" >
        <div className={`flex items-start gap-4 ${ user === props.message.name ? 'justify-end' : ''}`}>
            <div className="w-10 h-10 rounded-full overflow-hidden">
                <img src="https://daisyui.com/images/stock/photo-1534528741775-53994a69daeb.jpg" alt="Chat Avatar" className="w-full h-full object-cover" />
            </div>
            <div className="flex flex-col">
                <div className="flex items-baseline gap-2">
                    <span className="font-medium">{props.message.name}</span>
                    <time className="text-xs text-gray-500">12:45</time>
                </div>
                <div className={`font-medium rounded-lg p-2 ${user === props.message.name ? 'bg-purple-300 mr-8' : 'bg-purple-400'}`}>
                    {props.message.messageContent}
                </div>
            </div>
        </div>
    </div>

    )
}

export default Message
