import React,{useState,useEffect, useContext} from "react";
// import Albums from "../mainSpace/album";
// import Artists from "../mainSpace/artists";
// import Favorites from "../mainSpace/favorites";
// import image from "../..//Photos/image.png"
// import AlbumSongs from "../albumComponent/albumSongs";

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser, faPencil, faCheck } from '@fortawesome/free-solid-svg-icons' 

function User (user) {
	const [changeUsername, setChangeUsername] = useState(false)
	
	const toggleChangeUsername = () => {
		setChangeUsername(!changeUsername)
	}

	return(
		<div className="w-full">
			<div className="w-full">
				{(user.photo) ? 
					<div>
					</div>
					:
					<div className="flex flex-row m-10 items-center">
						<div className="bg-gradient-to-r from-black-200 to-blue-300 flex justify-center items-center border-2 border-black-500 rounded-full w-32 h-32 p-7">  
							<FontAwesomeIcon icon={faUser} className="text-5xl text-white" />
						</div>
						{ (!changeUsername) ?
							<div className="flex flex-row justify-center items-center">
								<h2 className="text-exo text-4xl text-white font-bold ml-10 select-none">
									{/* username */}
									mmoysis 
								</h2>
								<FontAwesomeIcon icon={faPencil} onClick={toggleChangeUsername} className="text-white text-md ml-3 mt-2.5 cursor-pointer select-none hover:text-gray-300" />
							</div>
							:
							<div className="flex flex-row ml-4">
								<input type="text" placeholder="mmoysis" className="rounded-sm pl-2 focus:outline-none h-7 w-full hover:border-2 hover:border-purple-300 focus:border-2 focus:border-purple-300" />
								<FontAwesomeIcon icon={faCheck} onClick={toggleChangeUsername} className="text-white text-md ml-3 mt-1.5 cursor-pointer select-none hover:text-gray-300" />
							</div>
						}
					</div>
				}
			</div>   
			<div className="flex flex-col gap-12 pt-4">
				<div className="flex justify-center items-center w-full">
					<div className="flex flex-col">
						<input placeholder="mmoysis@uth.gr" className="w-3/4 h-12 rounded-sm border-2 border-gray-200 bg-transparent pl-3 hover:border-white text-white"/>
					</div>
				</div>
				<div className="flex justify-center items-center w-full">
					<input placeholder="mmoysis@uth.gr" className="w-3/4 h-12 rounded-sm border-2 border-gray-200 bg-transparent pl-3 hover:border-white text-white"/>
				</div>
				<div className="flex justify-center items-center w-full">
					<input placeholder="mmoysis@uth.gr" className="w-3/4 h-12 rounded-sm border-2 border-gray-200 bg-transparent pl-3 hover:border-white text-white"/>
				</div>
			</div>
		</div>
	)
}

export default User
