import React,{useState} from "react";
import FriendsComp from "./friendComponent/friendComp";
import image1 from "../..//Photos/image.png"


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';

function FriendsBasic (props){
    const [friends,setFriends] = useState ([
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Moysis Moysis",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        },
        {
            name:"Kostas Kafantaris",
            icon:image1
        }
    ]);
    
    const addFriends=()=>{
        props.setFriendsPage("friendsAdd");
    }
    const openChat=(name)=>{
        props.setRender("chat");
        props.setFriend(name);
    }


    return(
        <div className="flex select-none flex-col h-full">
            <div className="flex flex-row text-xl ml-5 mr-5 mt-5">
                <p className="font-exo text-white">Friends</p>
                <i className="fas fa-user-plus text-white ml-auto cursor-pointer" onClick={() => addFriends()}></i>
            </div>
            <hr></hr>
            <div className="text-white flex flex-row flex-wrap justify-center gap-4 overflow-y-auto h-5/6 mt-5">
                {friends.map((friend,index) => (
                <div key={index} onClick={() => openChat(friend.name)}>
                    <FriendsComp 
                        icon = {friend.icon}
                        name = {friend.name} 
                    />
                </div>
                ))}
            </div>
        </div>
    )
}

export default FriendsBasic;
