import React, { useContext, useEffect, useState } from 'react'
import { io } from "socket.io-client"
import axios from 'axios'

import { RenderContext } from '../../Pages/musicHome';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import MessageBar from '../chat/messagebar';
import Message from "../chat/message"


function Chat (props) {
    const { socket } = useContext(RenderContext)
    const [messages, setMessages] = useState('')

    useEffect(() => {
        const accessToken = localStorage.getItem('accessToken')
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`

        axios.post("http://100.91.43.32:4000/chat/get-conversation-id", { friend: props.friendsName })
        .then(response => {
            socket.on('message', (message) => {
                setMessages(prevMessages => [...prevMessages, message])
            })
        })
        .catch(error => {

        })
    })

    // Go to main page.
    const musicHome = () => {
        if (props) {
            props.setChoice('mainSpace')
        }
    }

    return(
        <div className="flex flex-col flex-wrap text-white h-full w-full rounded-3xl" >
            <div className="flex flex-row gap-4 items-center text-xl ml-4 pt-4">
                <FontAwesomeIcon icon={faArrowLeft} className='cursor-pointer' onClick={musicHome} />
                <p> {props.friendsName} </p>
            </div>
            <div className='mt-4 h-4/5 w-4/6 mx-auto'>
                <div className="h-full w-full">
                    {messages.map(message =>(
                        <div className="flex flex-col overflow-y-auto">
                            <Message key={message.MessageID}
                                    message={message}/>
                        </div>
                    ))}
                </div>
            </div>
            
            <div className=" bottom-0 py-5 rounded-b-3xl w-full">
                <MessageBar/>
            </div>
        </div>
    )
}

export default Chat;
