import React, { useState } from "react"
import axios from "axios";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck } from '@fortawesome/free-solid-svg-icons'

function FriendsCompAdd (props){
    const acceptFriend = () => {
        const accessToken = localStorage.getItem('accessToken')
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`

        // IP with tailscale.
        axios.post("http://100.91.43.32:4000/user/confirm-friend", { usernameSec: props.name })
    }

    const declineFriend = () => {
        const accessToken = localStorage.getItem('accessToken')
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`

        // IP with tailscale.
        axios.post("http://100.91.43.32:4000/user/decline-request", { usernameSec: props.name })
    }

    return(
        <div className="flex flex-row cursor-pointer select-none hover:bg-purple-transparent rounded-md h-full gap-4 align-center items-center">
            <div className="h-10 w-10 ml-2 rounded-full">
                <img src={props.icon} alt={props.name} className="h-full w-full rounded-full" />
            </div>
            <div className="flex flex-col">
                <p>{props.name}</p>
                <div className="flex flex-row gap-2">
                    <button className="text-white bg-purple-900 rounded-md pl-1 mr-1 cursor-pointer" onClick={acceptFriend}> Accept </button>
                    <button className="text-white ml-1 bg-gray-700 rounded-md pl-1 pr-1 cursor-pointer" onClick={declineFriend}> Reject </button>
                </div>
            </div>
        </div>
    )
}
export default FriendsCompAdd;
