import MainSpace from "./mainSpace.js";
// import Chat from "../components/mainSpace/chat.js";

function MusicHome () {
    const[render,setRender] = useState("mainSpace");
    const[friend,setFriend] = useState();

    const handleRender = (items) =>{
        setRender(items);
    }


    const renderComponent = () => {
        switch (render) {
            case 'mainSpace':
                return <MainSpace name="mmoysis" />;
            default:
                return null;
        }
    }
    return(
        <div className="flex flex-col gap-2 h-screen w-screen bg-black">
            <div class="top-div">
                <Navbar />
            </div> 
            <div class="flex flex-row rounded-full gap-4 h-5/6 w-6/6 ml-6 mr-6 ">
                <div class="flex flex-col gap-4 w-1/6">  

                </div>
                {/* ΕΔΩ ΠΕΡΑ ΠΡΕΠΕΙ ΝΑ ΜΠΕΙ ΕΝΑΛΛΑΓΗ ΣΕ COMPONENT ΑΝΤΙ ΜΕ RENDERCOMPONENT ΑΝΑΜΕΣΑ ΣΕ MAINSPACE ΚΑΙ CHAT */}
                <div class="flex-1 rounded-3xl bg-gradient-to-b from-purple-400 to-black-200 shadow-lg" style={{boxShadow: 'inset 0 2px 4px rgba(0, 0, 0, 0.06), 0 2px 6px rgba(153, 50, 204, 0.5)' }} >
                    {renderComponent()}
                </div>
            </div>
        </div>
    )
}

export default MusicHome
