import React, { useState, useRef, useEffect } from "react";

import SearchRecent from '../searchRecentComponent/searchRecent'

import image from '../../Photos/image.png'
import imageEminem from '../../Photos/eminem.png'

function SearchIdle (props) {

    const [AllRecentSongs, setAllRecentSongs] = useState([
        { title: "Shape of You", artist: "Ed Sheeran" },
        { title: "Blinding Lights", artist: "The Weeknd" },
        { title: "Dance Monkey", artist: "Tones and I" },
        // Add more songs as needed
    ]);

    const [AllRecentArtists, setAllRecentArtists] = useState([
        {
            name: "Pink Floyd",
            image : {imageEminem},
            music: ["Progressive Rock", "Psychedelic Rock"],
            famousSongs: ["Comfortably Numb", "Wish You Were Here", "Another Brick in the Wall"]
        },
        {
            name: "The Beatles",
            image : {imageEminem},
            music: ["Rock", "Pop"],
            famousSongs: ["Hey Jude", "Let It Be", "Yesterday"]
        },
        {
            name: "Miles Davis",
            image : {imageEminem},
            music: ["Jazz", "Fusion"],
            famousSongs: ["So What", "All Blues", "Freddie Freeloader"]
        },
    ]);

    const [AllRecentAlbum,setAllRecentAlbum] = useState([
        {
            title: "Echoes",
            artist: "Pink Floyd",
            genre: "Progressive Rock",
            year: 1971,
            time:"40:32",
            tracks: ["Speak to Me", "Breathe", "On the Run", "Time", "The Great Gig in the Sky", "Money", "Us and Them", "Any Colour You Like", "Brain Damage", "Eclipse"]
        },
        {
            title: "Thriller",
            artist: "Michael Jackson",
            genre: "Pop",
            year: 1982,
            time:"40:32",
            tracks: ["Wanna Be Startin' Somethin'", "Baby Be Mine", "The Girl Is Mine", "Thriller", "Beat It", "Billie Jean", "Human Nature", "P.Y.T. (Pretty Young Thing)", "The Lady in My Life"]
        },
        {
            title: "Back in Black",
            artist: "AC/DC",
            genre: "Hard Rock",
            year: 1980,
            time:"40:32",
            tracks: ["Hells Bells", "Shoot to Thrill", "What Do You Do for Money Honey", "Given the Dog a Bone", "Let Me Put My Love into You", "Back in Black", "You Shook Me All Night Long", "Have a Drink on Me", "Shake a Leg", "Rock and Roll Ain't Noise Pollution"]
        }
    ])

    //handle the click of the Artist
    const handleArtistClick = (artist) => {

        props.setSelectedArtist(artist);
        props.setChoice("ArtistsSongs");
    };

    //handle the click of the Album
    const handleAlbumClick = (album) => {

        props.setSelectedAlbum(album);
        props.setChoice("AlbumSongs");
    };

    return (
        <div>
            <div className="text-white mb-4 mt-16 ml-24 text-xl">
            <p className="font-bold">Recently Searched</p>
            </div>
            <div className="flex flex-row flex-wrap text-white gap-4 overflow-x-auto ml-20 mt-12">
                {AllRecentSongs.map((song, index) => (
                    <div key={index} className="text-white">
                        <SearchRecent 
                            image = {image} //manually add the image will check later
                            title = {song.title}
                            artist = {song.artist}
                        />
                    </div>
                ))}
                {AllRecentArtists.map((artist, index) => (
                    <div key={index} className="text-white" onClick={() => handleArtistClick(artist)}>
                        <SearchRecent 
                            image = {imageEminem} //manually add the image will check later
                            title = {artist.name}
                            artist = "Artist"
                        />
                    </div>
                ))}
                {AllRecentAlbum.map((album, index) => (
                    <div key={index} className="text-white" onClick={() => handleAlbumClick(album)}>
                        <SearchRecent 
                            image = {image} //manually add the image will check later
                            title = {album.title}
                            artist = {album.artist}
                        />
                    </div>
                ))}
            </div>
        </div>
    )
}

export default SearchIdle;
