import React, { useState } from 'react';

// Assuming FontAwesomeIcon and faPause are used elsewhere or will be used
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPause } from '@fortawesome/free-solid-svg-icons';

import AlbumCover from "../Photos/Eminem_album_cover.jpg"

// Import custom style for the bar handle
import '../styles/handle.css';

function MusicBar(song) {
	const [value, setValue] = useState(0);

	// Ensure there's always a small segment for the purple to show at the beginning
	const percentage = value === 0 ? 0 : (value / song.duration) * 100;

	// Updated sliderStyle to correctly reflect the initial appearance and dynamic changes
	const sliderStyle = {
	  background: value === 0 ? '#393e46' : `linear-gradient(to right, purple ${percentage}%, #393e46 ${percentage}%)`, // This ensures the right side starts with #393e46
	  cursor: 'pointer',
	  appearance: 'none',
	  width: '100%',
	  height: '6px',
	  borderRadius: '9999px',
	};

	return (
		<div className='flex flex-col w-full justify-center items-center'>
			<div className='flex flex-row w-full justify-center items-center'>
				
			</div>
			<div className="flex flex-row w-full mt-4 px-4">
				<input
					type="range"
					min="0"
					max={song.duration}
					step="1"
					value={value}
					onChange={(e) => setValue(e.target.value)}
					style={sliderStyle}
					className='slider-thumb'
				/>
			</div>
		</div>
	);
}

export default MusicBar;
