import React,{useEffect,useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import MessageBar from '../chat/messagebar';
import MessageBox from '../chat/messageBox';


function Chat(props){
    

    return(
        <div className="flex flex-col text-white h-full w-full  relative rounded-3xl" >
            <div className="flex flex-row gap-4 items-center text-xl ml-4 pt-4">
                <FontAwesomeIcon icon={faArrowLeft} />
                <p>{props.friendsName}</p>
            </div>
            <div className='mt-4 h-4/5 w-4/6 mx-auto'>
                <MessageBox/>
            </div>
            
            <div className="absolute bottom-0 py-5 rounded-b-3xl w-full">
                <MessageBar/>
            </div>
        </div>
    )
}

export default Chat;
