import React, { useState } from "react"

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-solid-svg-icons' 

function UserDropdownMenu () {
    const [isOpen, setIsOpen] = useState(false)
    const [userPhoto, setUserPhoto] = useState()
  
    // Function to toggle the dropdown menu
    const toggleDropdown = () => {
      setIsOpen(!isOpen);
    }
  
    const logOut = () => {
    }

    return (
        <div className="relative">
            { (userPhoto) ?
                <img 
                    onClick={toggleDropdown} 
                    className="text-grey-400 rounded-full transform -translate-x-1/2 hover:text-grey-500 hover:cursor-pointer hover:border-grey-500 z-10 w-10 h-10"
                    src={userPhoto}
                />
                : 
                <FontAwesomeIcon icon={faUser} />
            }

            {/* Dropdown content */}
            {isOpen && (
                <div className="absolute top-full right-0 mt-2 w-48 bg-white shadow-md rounded overflow-hidden">
                    <ul className="list-none p-0 m-0">
                    <li className="text-start hover:bg-grey-200 cursor-pointer py-2 px-4 select-none">My account</li>
                    <li className="text-start hover:bg-grey-200 cursor-pointer py-2 px-4 select-none">Appointments</li>
                    <li className="text-start hover:bg-grey-200 cursor-pointer py-2 px-4 select-none">My favourites</li>
                    <li className="text-start hover:bg-grey-200 cursor-pointer py-2 px-4 select-none">My Reviews</li>
                    <hr/>
                    <li className="text-start hover:bg-grey-200 cursor-pointer pb-2 pt-0 px-4 select-none" onClick={logOut} > Log out </li>
                    </ul>
                </div>
            )}

        </div>
    );
}

export default UserDropdownMenu;
