import React, { useState, useRef, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouse } from '@fortawesome/free-solid-svg-icons';

function Search ({ data, onSearch }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [showSearchBar, setShowSearchBar] = useState(true);
    const searchRef = useRef(null);
  
    useEffect(() => {
      const handleClickOutside = (event) => {
        if (searchRef.current && !searchRef.current.contains(event.target)) {
          setShowSearchBar(false);
        }
      };
  
      document.addEventListener('mousedown', handleClickOutside);
      return () => {
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }, []);
  
    const handleChange = (event) => {
      setSearchTerm(event.target.value);
    };
  
    const handleSearchButtonClick = () => {
      setShowSearchBar(true);
    };
  
    const handleSearch = () => {
      const filteredData = data.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      onSearch(filteredData);
    };

    return (
        <div className="text-white text-lg justify-center items-center text-center mt-12">
            <div className="flex justify-center">
                <div className="relative flex  items-center">
                    <input
                        className="pl-2 pr-10 rounded-3xl text-2xl text-black focus:outline-none "
                        type="text"
                        placeholder="Search..."
                        value={searchTerm}
                        onChange={handleChange}
                    />
                    <button
                        onClick={handleSearch}
                        className="bg-transparent text-black text-2xl px-3 hover:text-purple-800 hover:text-3xl"
                        style={{ position: 'absolute', right: '0', top: '50%', transform: 'translateY(-50%)' }}
                        >
                        ⌕
                    </button>
                </div>
            </div>
        </div>
    );
  
  
  
    // return (
