    //                 <input className="pl-2 w-2/5 rounded-3xl text-2xl text-black"
    //                     type="text"
    //                     placeholder="Search..."
    //                     value={searchTerm}
    //                     onChange={handleChange}/>
    //                 <button onClick={handleSearch} className="ml-2 text-2xl">⌕</button>
    //             </div>
    //     </div>
    // )
}

export default Search;
