// Import basics.
import React, { useState } from "react";
import { useNavigate } from "react-router-dom"

// Import components.
import Navbar from "../components/HomePage/navbar"

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons' 

// Import photos.
import Background from "../Photos/LogIn.jpg"

// Login form.
function Login () {  
    const navigate = useNavigate()
    const [passwordShown, setPasswordShown] = useState(false)

    // Navigate to register page. Replace in
    // the history.
    const navigateRegister = () => {
        navigate("/register", { replace: true })
    }

    // Change the showing of the password
    // and confirm password.
    const showPassword = () => {
      setPasswordShown(!passwordShown);
    }

    return (
        <div className="log-container w-screen h-screen flex flex-col" style={{backgroundImage: `url(${Background})`, backgroundSize: 'cover', backgroundPosition: 'center'}}>
            <Navbar loginScreen={true} />
            <div className="flex flex-col items-center h-full">
                <div className="log-box">
                    <div className="pt-5 select-none">
                        <p className="text-5xl font-bold font-exo text-white"> Log In to Echobond </p>
                    </div>
                    <div className="m-4">
                        <div className="h-fit py-2 px-5">
                            <form className="flex flex-col justify-center items-center pt-10 gap-2">
                                <div className="pb-3 w-fit"> 
                                <input
                                    className="h-10 w-64 rounded-full select-none pl-4 font-exo text-md focus:outline-none border-2 border-gray hover:border-2 hover:border-purple-200 focus:border-2 focus:border-purple-200"
                                    type="text"
                                    placeholder="Username"
                                />
                                {/* <i className="fa-icon"> <FontAwesomeIcon icon={faUser} /> </i>  */}
                                </div>
                                <div className="relative w-fit">
                                    <input
                                        className="h-10 w-64 rounded-full select-none pl-4 pr-10 text-md font-exo select-none border-2 border-gray hover:border-2 hover:border-purple-200 focus:outline-none focus:border-2 focus:border-purple-200"
                                        type={passwordShown ? 'text' : 'password'}
                                        placeholder="Password"
                                    />
                                    <i
                                        className="absolute inset-y-0 right-0 pr-3 flex items-center text-sm leading-5"
                                        onClick={showPassword}
                                    >
                                        <FontAwesomeIcon icon={passwordShown ? faEyeSlash : faEye} className="cursor-pointer" />
                                    </i>
                                </div>
                                <button className="text-white font-nunito w-fit rounded-2xl bg-purple-200 hover:bg-purple-300 px-5 py-1.5 mt-7 select-none"> Log In </button>
                                <div className="flex flex-row mt-2">
                                    <button className="outline-none bg-transparent underline text-white font-exo font-bold hover:text-purple-200"> Forgot Password </button>
                                </div>
                                <hr className="border-1 border-purple-300 w-full mt-4" />
                                <div className="flex flex-row mt-1">
                                    <p className="text-white text-md font-exo"> If you are not already a member </p>
                                    <button onClick={navigateRegister} className="ml-3 outline-none bg-transparent underline text-white hover:text-purple-200 font-exo font-bold"> Sign up </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login
