import React from 'react';

const Slider = ({
  colorAfter = '#9818d6',
  colorBefore = '#9818d6',
  highlighted = '#9F7AEA',
  value,
  onChange, // Ensure to handle value change
}) => {
  const percent = value * 100;
  
  // Inline style for the gradient background of the slider
  const sliderStyle = {
    background: `linear-gradient(to right, ${highlighted} 0%, ${highlighted} ${percent}%, ${colorAfter} ${percent}%, ${colorAfter} 100%)`,
  };

  return (
    <div> {/* Adjust height as needed */}
        <input
          className="w-full h-1 rounded-full cursor-pointer appearance-none"
          type="range"
          onChange={onChange}
          value={value}
          style={sliderStyle}
        />
    </div>
  );
};

export default Slider;
