import React, { useState } from 'react';
import styled from 'styled-components';

const transition = 'height 0.15s 0s ease, width 0.15s 0s ease';

const StyledSlider = styled.input`
  cursor: pointer;
  background: linear-gradient(
    to right,
    ${(props) => props.highlighted} 0%,
    ${(props) => props.highlighted} ${(props) => props.percent}%,
    ${(props) => props.colorAfter} ${(props) => props.percent}%,
    ${(props) => props.colorAfter} 100%
  );
  border-radius: 8px;
  height: 4px;
  width: 100%;
  outline: none;
  padding: 0;
  margin: 5px 10px;
  transition: ${transition};
  -webkit-appearance: none;

  &::-webkit-slider-thumb {
    border: none;
    -webkit-appearance: none;
    width: ${(props) => props.seeking ? props.growTo : props.size}px;
    height: ${(props) => props.seeking ? props.growTo : props.size}px;
    cursor: pointer;
    background: ${(props) => props.colorBefore};
    border-radius: 50%;
  }

  &::-moz-range-thumb {
    border: none;
    width: ${(props) => props.seeking ? props.growTo : props.size}px;
    height: ${(props) => props.seeking ? props.growTo : props.size}px;
    cursor: pointer;
    background: ${(props) => props.colorBefore};
    border-radius: 50%;
  }

  &::-ms-thumb {
    border: none;
    width: ${(props) => props.seeking ? props.growTo : props.size}px;
    height: ${(props) => props.seeking ? props.growTo : props.size}px;
    cursor: pointer;
    background: ${(props) => props.colorBefore};
    border-radius: 50%;
  }
`;

const Slider = ({
  colorAfter = '#E1E1E6',
  colorBefore = '#A5AAB2',
  highlighted = '#EB3E3E',
  size = 10,
  value,
  ...props
}) => {
  const percent = value * 100;
  const growTo = size + 4;

  const [hover, setHover] = useState(false);

  return (
    <StyledSlider
      type='range'
      onMouseOver={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      value={value}
      size={size}
      colorAfter={colorAfter}
      colorBefore={colorBefore}
      highlighted={highlighted}
      percent={percent}
      growTo={growTo}
      seeking={hover}
      {...props}
    />
  );
}

export default Slider;
