import React, { useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPause, faShuffle, faPlay, faForwardStep, faBackwardStep, faRepeat } from '@fortawesome/free-solid-svg-icons';
import '../styles/handle.css';

function MusicBar({ duration }) {
    const [value, setValue] = useState(0);
    const [randomSelected, setRandomSelected] = useState(false);
    const [repeatSelected, setRepeatSelected] = useState(false);
    const [isHovered, setIsHovered] = useState(false);
    const [playing, setPlaying] = useState(true);

    const audioRef = useRef(null);

    const percentage = value === 0 ? 0 : (value / duration) * 100;

    const changeRandom = () => {
        setRandomSelected(!randomSelected);
    };

    const changeRepeat = () => {
        setRepeatSelected(!repeatSelected);
    };

    const changePlaying = () => {
        setPlaying(!playing);
        if (playing) {
            audioRef.current.pause();
        } else {
            audioRef.current.play();
        }
    };

    const sliderStyle = {
        background: isHovered ? `linear-gradient(to right, #9818d6 ${percentage}%, #393e46 ${percentage}%)` : `linear-gradient(to right, white ${percentage}%, #393e46 ${percentage}%)`,
        cursor: 'pointer',
        appearance: 'none',
        width: '100%',
        height: '4px',
        borderRadius: '9999px',
    };

    return (
        <div className='flex flex-col w-full justify-center items-center'>
            <div className='flex flex-row w-full justify-center items-center gap-5'>
                <FontAwesomeIcon icon={faShuffle} onClick={changeRandom} className={`${randomSelected ? 'text-purple-200' : 'text-gray-200'} w-5 h-5 cursor-pointer select-none`} />
                <FontAwesomeIcon icon={faBackwardStep} className='text-gray-200 hover:text-white w-5 h-5 cursor-pointer select-none' />
                <FontAwesomeIcon icon={playing ? faPause : faPlay} onClick={changePlaying} className='text-white w-5 h-5 cursor-pointer select-none' />
                <FontAwesomeIcon icon={faForwardStep} className='text-gray-200 hover:text-white w-5 h-5 cursor-pointer select-none' />
                <FontAwesomeIcon icon={faRepeat} onClick={changeRepeat} className={`${repeatSelected ? 'text-purple-200' : 'text-gray-200'} w-5 h-5 cursor-pointer select-none`} />
            </div>
            <div className="flex flex-row w-full mt-5 px-4">
                <audio ref={audioRef} src="./Arsenal.mp3" controls>
                    Your browser does not support the audio element.
                </audio>
                <input
                    type="range"
                    min="0"
                    max={duration}
                    step="0.1"
                    value={value}
                    onMouseEnter={() => setIsHovered(true)}
                    onMouseLeave={() => setIsHovered(false)}
                    onChange={(e) => setValue(e.target.value)}
                    style={sliderStyle}
                    className='slider-thumb mt-2.5 w-full'
                />
            </div>
        </div>
    );
}

export default MusicBar;
