import React,{useState} from "react"

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser, faPencil, faCheck } from '@fortawesome/free-solid-svg-icons' 

function FriendsComp(props){
    return(
        <div className="flex flex-row cursor-pointer select-none hover:bg-purple-transparent rounded-md h-full gap-4 align-center items-center py-2">
            {props.icon ?
                <div className="h-10 w-10 ml-5 rounded-full">
                    <img src={props.icon} alt={props.name} className="h-full w-full rounded-full" />
                </div>
                :
                <div className="bg-gradient-to-r from-purple-200 to-blue-200 flex justify-center items-center border-2 border-black-500 rounded-full w-32 h-32 p-7">
                    <FontAwesomeIcon icon={faUser} className="text-5xl text-white" />
                </div>
            }
            <div>
                <p>{props.name}</p>
            </div>
        </div>
    )
}
export default FriendsComp;
