import React, { useState, useRef } from 'react';

const MusicProgressBar = ({ duration, current, onSeek }) => {
  const barRef = useRef(null);
  const [progress, setProgress] = useState((current / duration) * 100);

  // Function to update progress when the bar itself is clicked
  const updateProgress = (e) => {
    const rect = barRef.current.getBoundingClientRect();
    const newX = e.clientX - rect.left;
    const newProgress = (newX / rect.width) * 100;
    setProgress(newProgress);
    onSeek((newProgress / 100) * duration); // Callback for seeking
  };

  return (
    <div ref={barRef} onClick={updateProgress} className="relative w-full bg-gray-300 h-2 rounded-full cursor-pointer">
      <div className="absolute bg-blue-600 h-2 rounded-full" style={{ width: `${progress}%` }}></div>
      <div 
        className="absolute w-4 h-4 bg-blue-600 rounded-full shadow"
        style={{ left: `${progress}%`, transform: 'translateX(-50%)', top: '-0.5rem', cursor: 'pointer' }} 
        draggable
        onDrag={(e) => updateProgress(e)}
      >
        {/* Handle */}
      </div>
    </div>
  );
};

export default MusicProgressBar
