import React, { useState, useRef, useEffect } from "react";

import Song from "../albumComponent/song";
import Album from "../albumComponent/albumComponent";
import Artists from "../artistsComponent/artistsComponent";
import axios from "axios";
import image from '../../Photos/image.png'
import imageEminem from '../../Photos/eminem.png'

function SearchHandler (props) {
    const[tracks,setTracks] = useState([])
    const[albums,setAlbums] = useState([])
    const[artists,setArtists] = useState([])

    useEffect(() =>{
        console.log("trying to search items with name" + props.value)
        axios.get('http://100.91.43.32:4000/search/get-search?value='+props.value)
        .then(response=>{
            setTracks(response.data.songs)
            setArtists(response.data.artists)
            setAlbums(response.data.albums)
        })
    },[props.trigger])

    //handle the click of the Artist
    const handleArtistClick = (artist) => {
        //setting the page to mainSpace and putting ArtistSongs
        props.setSelectedArtist(artist);
        props.setMainSpace("mainSpace")
        props.setChangeTo("ArtistsSongs");


        axios.put("http://100.91.43.32:4000/search/search-items",{name : artist.name , type:"artist"})
        .then(response=>{
            console.log("All good")
        })
        .catch(error => {
            console.log(error)
        })
    }

    //handle the click of the Song
    const handleSongClick = (song) => {

        axios.put("http://100.91.43.32:4000/search/search-items",{name:song.name , type:"song"})
        .then(response=>{
            console.log("All good")
        })
        .catch(error => {
            console.log(error)
        })
    }

        //handle the click of the Album
        const handleAlbumClick = (album) => {
            props.setSelectedAlbum(album);
            //setting the page to mainSpace and putting AlbumSongs
            props.setMainSpace("mainSpace")
            props.setChangeTo("AlbumSongs");
    
            axios.put("http://100.91.43.32:4000/search/search-items",{name : album.name , type:"album"})
            .then(response=>{
                console.log("All good")
            })
            .catch(error => {
                console.log(error)
            })
        }
    
    return (
        <div className="flex flex-col h-full">
            <div className="text-white mb-4 mt-4 ml-24 text-2xl">
                <p className="font-bold">Your Results :</p>
            </div>
            <div className="flex-wrap overflow-y-auto mb-24"> 
                <div className="flex flex-col flex-wrap text-white gap-4 ml-16 mt-4">
                    {tracks.map((track ) => (
                        <div key={track} onClick={() => handleSongClick(track)}>
                            <Song 
                                name={track.name}
                                time ={track.time}
                                image={track.image}
                                artist={track.artist}
                                />
                        </div>
                    ))}
                </div>
                <div className="flex flex-col flex-wrap text-white gap-4 overflow-x-auto ml-16 mt-4">
                    {albums.map((album ) => (
                        <div key={album} onClick={() => handleAlbumClick(album)}>
                            <Album 
                                name = {album.name}
                                genre = {album.style}
                                image = {album.image}
                                year = {album.year}
                                artist = {album.artist}
                                />
                        </div>
                    ))}
                </div>
                <div className="flex flex-col flex-wrap text-white gap-4 overflow-x-auto ml-16 mt-4">
                    {artists.map((artist ) => (
                        <div key={artist} onClick={() => handleArtistClick(artist)}>
                            <Artists 
                                name={artist.name}
                                image={artist.image}
                                music={artist.style}
                                />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default SearchHandler
