import React from 'react';

import "../styles/handle.css"

const Slider = ({
  value,
  onChange, // Function to handle value change
}) => {
  const percent = value * 100;

  // Inline style for the slider thumb and track
  const sliderThumbStyle = {
    // Thumb color
    '--tw-range-thumb-background': 'purple',
    '--tw-range-thumb-border-color': 'transparent', // Remove border if needed
  };

  // Inline style for the slider track
  const sliderTrackStyle = {
    background: `linear-gradient(to right, #E1E1E6 ${percent}%, #A5AAB2 ${percent}%)`,
  };

  return (
    <div className="flex items-center justify-center">
      <div className="w-full max-w-lg px-4">
        <input
          className="w-full h-1 rounded-full cursor-pointer appearance-none bg-gray-300 range-thumb:bg-purple range-track:bg-gray-300"
          type="range"
          onChange={onChange}
          value={value}
          style={{...sliderThumbStyle, ...sliderTrackStyle}}
        />
      </div>
    </div>
  );
};

export default Slider;
