import React, { useState, useRef, useEffect } from "react";

import SearchIdle from "../searchComponent/searchIdle.js";
import SearchHandler from "../searchComponent/searchHandler.js"
import AlbumSongs from "../albumComponent/albumSongs.js";
import ArtistsSongs from "../artistsComponent/artistsSongs.js";

import image from '../../Photos/image.png'

function Search (props, { data, onSearch }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [choice, setChoice] = useState('SearchIdle');
    const [selectedAlbum, setSelectedAlbum] = useState(null);
    const [selectedArtist, setSelectedArtist] = useState(null);

    const handleSearch = () => {
        // const filteredData = data.filter(item =>
        // item.name.toLowerCase().includes(searchTerm.toLowerCase())
        // );
        // onSearch(filteredData);
        setChoice('SearchHandler');
    };

    const renderComponent = () => {
        switch (choice) {
            //avoid error if its called from mainspace and not here
            case 'Artists' :
            case 'Albums' :
            case 'SearchIdle' : 
                return <SearchIdle setChoice={setChoice} setSelectedAlbum={setSelectedAlbum} setSelectedArtist={setSelectedArtist}/>;
            case 'SearchHandler' :
                return <SearchHandler/>
            case 'AlbumSongs':
                return <AlbumSongs
                        image = {image}
                        title = {selectedAlbum.title}
                        artist= {selectedAlbum.artist}
                        year = {selectedAlbum.year}
                        gerne = {selectedAlbum.gerne}
                        setChoice={setChoice}
                />; 
            case 'ArtistsSongs':
                return <ArtistsSongs 
                        name = {selectedArtist.name}
                        image = {image}
                        setSelectedAlbum={setSelectedAlbum}
                        setChoice={setChoice}
                />
            default:
                return null;
        }
    }

    return (
        <div className="flex flex-col h-full">
            <div className="text-white text-lg justify-center items-center text-center mt-12">
                <div className="flex justify-center">
                    <div className="relative flex  items-center">
                        <input
                            className="pl-2 pr-10 rounded-3xl text-2xl text-black focus:outline-none "
                            type="text"
                            placeholder="Search..."
                            value={searchTerm}
                            onChange=  "" /*{handleChange}*/
                        />
                        <button
                            onClick={handleSearch}
                            className="bg-transparent text-black text-2xl px-3 hover:text-purple-800 hover:text-3xl"
                            style={{ position: 'absolute', right: '0', top: '50%', transform: 'translateY(-50%)' }}
                            >
                            âŒ•
                        </button>
                    </div>
                </div>
            </div>
            <div className="h-5/6  rounded-3xl mr-4 ml-4 mt-4">
                {renderComponent()}
            </div>
        </div>
    )
}

export default Search;

