import React,{useState,useEffect, useContext} from "react";

import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import { jwtDecode } from 'jwt-decode';
import CountrySelection from "../components/countrySelection";

// For toast.
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Import icons.
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser, faPencil, faCheck } from '@fortawesome/free-solid-svg-icons' 

function User (user) {
	const [changeUsername, setChangeUsername] = useState(false)
    const navigate = useNavigate()
	
	const toggleChangeUsername = () => {
		setChangeUsername(!changeUsername)
	}

    // Get username from the token.
    function getUsernameFromToken() {
        const accessToken = localStorage.getItem('accessToken');
        if (!accessToken) {
            return null;
        }
    
        try {
            const decodedToken = jwtDecode(accessToken)
            return decodedToken.username; // Assuming the username is stored in the token under 'username'
        } catch (error) {
            const refreshToken = localStorage.getItem('refreshToken');
            axios.defaults.headers.common['Authorization'] = `Bearer ${refreshToken}`

            axios.get("http://100.91.43.32:4000/token/refresh-token")
            .then(response => {
                localStorage.setItem("accessToken", response.data.accessToken)
                const decodedToken = jwtDecode(response.data.accessToken)
            
                return decodedToken.username; // Assuming the username is stored in the token under 'username'
            })
            .catch(error => {
                navigate("/", { replace: true })
            })
            
            return null;
        }
    }    

	return(
		<div className="w-full">
			<div className="w-full">
				{(user.photo) ? 
					<div>
					</div>
					:
					<div className="flex flex-row mx-10 mt-10 items-center">
						<input  
                            type="file"
                            accept="image/*"
                            onChange=""
                            className="bg-gradient-to-r from-purple-200 to-blue-200 flex justify-center items-center border-2 border-black-500 rounded-full w-32 h-32 p-7"
                        />
                        

						{ (!changeUsername) ?
							<div className="flex flex-row justify-center items-center">
								<h2 className="text-exo text-4xl text-white font-bold ml-10 select-none">
									{/* username */}
									{getUsernameFromToken()} 
								</h2>
								<FontAwesomeIcon icon={faPencil} onClick={toggleChangeUsername} className="text-white text-md ml-3 mt-2.5 cursor-pointer select-none hover:text-gray-300" />
							</div>
							:
							<div className="flex flex-row ml-4">
								<input type="text" placeholder="mmoysis" className="rounded-sm pl-2 focus:outline-none h-7 w-full bg-transparent hover:border-2 hover:border-purple-300 focus:border-2 focus:border-purple-300 text-white" />
								<FontAwesomeIcon icon={faCheck} onClick={toggleChangeUsername} className="text-white text-md ml-3 mt-1.5 cursor-pointer select-none hover:text-gray-300" />
							</div>
						}
					</div>
				}
			</div>   
			<div className="flex flex-col justify-center items-center mt-4">
				<div className="flex flex-col w-3/4 mt-2">
					<h1 className="font-bold font-exo text-white"> Email </h1>
					<input placeholder="mmoysis@uth.gr" className="w-full h-12 rounded-sm border-2 border-gray-200 bg-transparent pl-3 mt-2 hover:border-2 hover:border-purple-300 text-white"/>
				</div>
				<div className="flex flex-col w-3/4 mt-8">
					<h1 className="font-bold font-exo text-white"> Date of birth </h1>
					<div className="flex flex-row w-full mt-2 gap-5">	
						<input type="text" className="bg-transparent rounded-sm border-2 border-gray-200 hover:border-purple-300 w-1/3 h-12 text-white pl-3" />
						<select className="bg-transparent rounded-sm border-2 border-gray-200 hover:border-purple-300 w-1/3 h-12 pl-3 text-white"> 
							<option className="bg-black-200 border-none"> Select </option>
							<option className="bg-black-200 border-none"> January </option>
							<option className="bg-black-200 border-none"> February </option>
							<option className="bg-black-200 border-none"> March </option>
							<option className="bg-black-200 border-none"> April </option>
							<option className="bg-black-200 border-none"> May </option>
							<option className="bg-black-200 border-none"> June </option>
							<option className="bg-black-200 border-none"> July </option>
							<option className="bg-black-200 border-none"> August </option>
							<option className="bg-black-200 border-none"> September </option>
							<option className="bg-black-200 border-none"> October </option>
							<option className="bg-black-200 border-none"> November </option>
							<option className="bg-black-200 border-none"> December </option>
						</select>
						<input type="text" className="bg-transparent rounded-sm border-2 border-gray-200 hover:border-purple-300 w-1/3 h-12 pl-3 text-white" />
					</div>
				</div>	
				<div className="flex flex-col w-3/4 mt-8">
					<h1 className="font-bold font-exo text-white"> Country </h1>
					<CountrySelection className="mt-2" />
				</div>
				<button className="rounded-md bg-purple-200 hover:bg-purple-300 px-4 py-2 mt-16"> Change data </button>
			</div>
		</div>
	)
}

export default User
